contracts/PriceEstimator.sol.content:
pragma solidity 0.6.2;

import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Address.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "./IPriceEstimator.sol";

contract PriceEstimator is IPriceEstimator, Initializable, OwnableUpgradeSafe {
  using Address for address;

  //address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;

  IUniswapV2Router02 internal uniswapRouter;

  modifier onlyContract(address account)
  {
    require(account.isContract(), "[Validation] The address does not contain a contract");
    _;
  }

  function initialize(address uniswapRouterAddress)
  external
  onlyContract(uniswapRouterAddress)
  {
    __PriceEstimator_init(uniswapRouterAddress);
  }

  function __PriceEstimator_init(address uniswapRouterAddress)
  internal
  initializer
  {
    __Context_init_unchained();
    __Ownable_init_unchained();
    uniswapRouter = IUniswapV2Router02(uniswapRouterAddress);
  }

  function setUniswapRouter(address uniswapRouterAddress)
  external
  onlyOwner
  onlyContract(uniswapRouterAddress)
  {
    require(
      uniswapRouterAddress != address(0),
      "[Validation]: Invalid uniswap router address"
    );
    uniswapRouter = IUniswapV2Router02(uniswapRouterAddress);
  }

  function getEstimatedETHforERC20(uint256 erc20Amount, address tokenAddress)
  external
  override
  view
  returns (uint256[] memory)
  {
    return uniswapRouter.getAmountsIn(erc20Amount, getPathForETHtoERC20(tokenAddress));
  }

  function getPathForETHtoERC20(address tokenAddress)
  internal
  view
  returns (address[] memory)
  {
    address[] memory path = new address[](2);
    path[0] = uniswapRouter.WETH();
    path[1] = tokenAddress;
    return path;
  }

  function getEstimatedERC20forETH(uint256 etherAmount, address tokenAddress)
  external
  override
  view
  returns (uint256[] memory)
  {
    return uniswapRouter.getAmountsIn(etherAmount, getPathForERC20toETH(tokenAddress));
  }

  function getPathForERC20toETH(address tokenAddress)
  internal
  view
  returns (address[] memory)
  {
    address[] memory path = new address[](2);
    path[0] = tokenAddress;
    path[1] = uniswapRouter.WETH();
    return path;
  }
}

contracts/SmartSwap.sol.content:
pragma solidity 0.6.2;

import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Pausable.sol";
import "./IERC20Extended.sol";
import "./IPriceEstimator.sol";

contract SmartSwap is Initializable, OwnableUpgradeSafe, PausableUpgradeSafe {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using Address for address;

    enum Status {
        OPEN,
        CLOSED,
        CANCELLED
    }

    enum SwapType {
        ETH_TO_ERC20,
        ERC20_TO_ETH,
        ERC20_TO_ERC20
    }

    struct Swap {
        uint256 openValue;
        uint256 closeValue;
        address payable openTrader;
        address payable closeTrader;
        address openContractAddress;
        address closeContractAddress;
        SwapType swapType;
        Status status;
    }

    address constant private ETH_ADDRESS = address(
        0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE
    );

    uint256 constant private DEV_FEE_PERCENTAGE = 10;
    uint256 constant private BURN_FEE_PERCENTAGE = 10;

    //Global swap id. Also give total number of swaps made so far
    uint256 private _swapId;

    mapping (uint256 => Swap) private _swaps;

    IERC20 private _swapToken;

    //Wallet where fees will go
    address payable private _feesWallet;

    //Wallet where dev fund will go
    address private _devWallet;

    uint256 private _ethFeePercentage;
    uint256 private _allowedFeeSlippagePercentage;
    uint256 private _uniswapFeePercentage;

    IPriceEstimator private _priceEstimator;

    //list of free tokens
    mapping(address => bool) private _listFreeTokens;

    event Open(uint256 indexed id, address indexed openTrader, address indexed closeTrader);
    event Cancel(uint256 indexed id);
    event Close(uint256 indexed id);
    event FeeWalletChanged(address indexed wallet);
    event DevWalletChanged(address indexed wallet);
    event SwapTokenUpdated(address indexed swapTokenAddress);

    modifier onlyContract(address account)
    {
        require(account.isContract(), "[Validation] The address does not contain a contract");
        _;
    }

    modifier onlyOpenSwaps(uint256 id) {
        Swap memory swap = _swaps[id];
        require (swap.status == Status.OPEN);
        _;
    }

    /**
    * @dev initialize
    */
    function initialize(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    external
    onlyContract(swapTokenAddress)
    onlyContract(priceEstimator)
    {
        __SmartSwap_init(swapTokenAddress, feesWallet, devWallet, priceEstimator);
    }

    function __SmartSwap_init(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    internal
    initializer
    {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Pausable_init_unchained();
        __SmartSwap_init_unchained(swapTokenAddress, feesWallet, devWallet, priceEstimator);
    }

    function __SmartSwap_init_unchained(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    internal
    initializer
    {
        require(
            swapTokenAddress != address(0),
            "[Validation] Invalid swap token address"
        );
        require(feesWallet != address(0), "[Validation] feesWallet is the zero address");
        require(devWallet != address(0), "[Validation] devWallet is the zero address");
        require(
            priceEstimator != address(0),
            "[Validation] Invalid price estimator address"
        );

        _swapToken = IERC20(swapTokenAddress);
        _feesWallet = feesWallet;
        _devWallet = devWallet;
        _priceEstimator = IPriceEstimator(priceEstimator);
        _ethFeePercentage = 3;
        _allowedFeeSlippagePercentage = 5;
        _uniswapFeePercentage = 3;
    }

    /**
    * @dev Called by an admin to pause, triggers stopped state.
    */
    function pause()
    external
    onlyOwner 
    {
        _pause();
    }

    /**
    * @dev Called by an admin to unpause, returns to normal state.
    */
    function unpause()
    external
    onlyOwner
    {
        _unpause();
    }

    /**
    * @dev Update swap token address
    * @param swapTokenAddress New swap token address
    */
    function setSwapToken(address swapTokenAddress)
    external
    onlyOwner
    onlyContract(swapTokenAddress)
    {
        require(
            swapTokenAddress != address(0),
            "[Validation]: Invalid swap token address"
        );
        _swapToken = IERC20(swapTokenAddress);
        emit SwapTokenUpdated(swapTokenAddress);
    }

    /**
    * @dev Allows admin to set fee receiver wallet
    * @param wallet New wallet address
    */
    function setFeeWallet(address payable wallet)
    external
    onlyOwner
    {
        require(
            wallet != address(0),
            "[Validation] feesWallet is the zero address"
        );
        _feesWallet = wallet;

        emit FeeWalletChanged(wallet);
    }

    /**
    * @dev Allows admin to set fee receiver wallet
    * @param wallet New wallet address
    */
    function setDevWallet(address payable wallet)
    external
    onlyOwner
    {
        require(
            wallet != address(0),
            "[Validation] devWallet is the zero address"
        );
        _devWallet = wallet;

        emit DevWalletChanged(wallet);
    }

    /**
    * @dev Update price estimator address
    * @param priceEstimator New price estimator address
    */
    function setPriceEstimator(address priceEstimator)
    external
    onlyOwner
    onlyContract(priceEstimator)
    {
        require(
            priceEstimator != address(0),
            "[Validation]: Invalid price estimator address"
        );
        _priceEstimator = IPriceEstimator(priceEstimator);
    }

    /**
    * @dev Update fees
    * @param ethFeePercentage New percentage of fee in eth
    */
    function setEthFeePercentage(uint8 ethFeePercentage)
    external
    onlyOwner
    {
        require(
            ethFeePercentage >= 0 && ethFeePercentage <= 100,
            "[Validation]: ETH Fee percentage must be between 0 to 100"
        );
        _ethFeePercentage = ethFeePercentage;
    }

    /**
    * @dev Update fee slippage percentage allowance for erc20
    * @param allowedFeeSlippagePercentage New allowed fee slippage percentage for fee in erc20
    */
    function setAllowedFeeSlippagePercentage(uint8 allowedFeeSlippagePercentage)
    external
    onlyOwner
    {
        require(
            allowedFeeSlippagePercentage >= 0 && allowedFeeSlippagePercentage <= 100,
            "[Validation]: Allowed Fee Slippage percentage must be between 0 to 100"
        );
        _allowedFeeSlippagePercentage = allowedFeeSlippagePercentage;
    }

    /**
    * @dev Update Uniswap fees
    * @param uniswapFeePercentage New percentage of uniswap fee
    */
    function setUniswapFeePercentage(uint8 uniswapFeePercentage)
    external
    onlyOwner
    {
        require(
            uniswapFeePercentage >= 0 && uniswapFeePercentage <= 100,
            "[Validation]: Uniswap Fee percentage must be between 0 to 100"
        );
        _uniswapFeePercentage = uniswapFeePercentage;
    }

    function getFeeInEthForEth(uint256 amount)
    public
    view
    returns (uint256) 
    {
        //_ethFeePercentage is a percentage expressed in 1/10 (a tenth) of a percent hence we divide by 1000
        return amount.mul(_ethFeePercentage).div(1000); //0.3% of ETH amount
    }

    function getFeeInEthForERC20(uint256 amount, address token)
    public 
    view
    returns (uint256)
    {
        if(isFreeToken(token)) {
            return 0;
        } else {
            //price should be estimated by 1 token because Uniswap algo changes price based on large amount
            uint256 tokenBits = 10 ** uint256(IERC20Extended(token).decimals());
            uint256 estFeesInEthPerUnit = _priceEstimator.getEstimatedETHforERC20(tokenBits, token)[0];
            //subtract uniswap 0.30% fees
            //_uniswapFeePercentage is a percentage expressed in 1/10 (a tenth) of a percent hence we divide by 1000
            estFeesInEthPerUnit = estFeesInEthPerUnit.sub(estFeesInEthPerUnit.mul(_uniswapFeePercentage).div(1000));
            uint256 equivEth = amount.mul(estFeesInEthPerUnit).div(tokenBits); //multiply by amount to be scheduled amount
            return getFeeInEthForEth(equivEth);
        }
    }

    function getFeeInEthForERC20UsingTotalSupply(uint256 amount, address token)
    public
    view
    returns (uint256)
    {
        if(isFreeToken(token)) {
            return 0;
        } else {
            //per 1% supply , 0.1 ETH is the fee
            uint256 tokenTotalSupply = IERC20(token).totalSupply();
            uint256 percentage = amount.mul(tokenTotalSupply).mul(100).div(tokenTotalSupply);
            uint256 ethFeeInWei = 100000000000000000; //0.1 ETH
            return percentage.mul(ethFeeInWei).div(tokenTotalSupply);
        }
    }

    function getFeeInSwapForETH(uint256 amount)
    public
    view
    returns (uint256)
    {
        uint256 feesInEth = getFeeInEthForEth(amount);
        return _getEquivSwapFee(feesInEth);
    }

    function getFeeInSwapForERC20(uint256 amount, address token, bool calcFeeUsingTotalSupply)
    public
    view
    returns (uint256)
    {
        uint256 feesInEth = calcFeeUsingTotalSupply ? 
            getFeeInEthForERC20UsingTotalSupply(amount, token) : 
            getFeeInEthForERC20(amount, token);
        return _getEquivSwapFee(feesInEth);
    }

    function _getEquivSwapFee(uint256 feesInEth)
    private
    view
    returns (uint256)
    {
        uint256 feesInEthIfPaidViaSwap = feesInEth.div(2);
        uint256 swapPerEth = _priceEstimator.getEstimatedERC20forETH(1, address(_swapToken))[0];
        //subtract uniswap 0.30% fees
        //_uniswapFeePercentage is a percentage expressed in 1/10 (a tenth) of a percent hence we divide by 1000
        uint256 estSwapPerEth = swapPerEth.sub(swapPerEth.mul(_uniswapFeePercentage).div(1000));
        return feesInEthIfPaidViaSwap.mul(estSwapPerEth);
    }

    function _distributeFees(uint256 fee)
    private
    {
        uint256 devAmount = fee.mul(DEV_FEE_PERCENTAGE).div(100); //10%
        uint256 burnAmount = fee.mul(BURN_FEE_PERCENTAGE).div(100); //10%
        uint256 remAmount = fee.sub(devAmount).sub(burnAmount); //80%

        _swapToken.safeTransferFrom(msg.sender, _feesWallet, remAmount);
        _swapToken.safeTransferFrom(msg.sender, _devWallet, devAmount);
        IERC20Extended(address(_swapToken)).burnFrom(msg.sender, burnAmount);
    }

    function open(
        uint256 openValue,
        address openContractAddress,
        uint256 closeValue,
        address payable closeTrader,
        address closeContractAddress,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    external
    payable
    whenNotPaused
    {
        require(openValue > 0, "[Validation] The open value has to be larger than 0");
        require(closeValue > 0, "[Validation] The close value has to be larger than 0");
        if(!isFreeToken(openContractAddress)) {
        require(fee > 0, "[Validation] The fee has to be larger than 0");
        }
        require(closeTrader != address(0), "[Validation] Invalid close trader address");

        if(ETH_ADDRESS == openContractAddress)
        {
            _openEtherToERC20(
                openValue,
                closeValue,
                closeTrader,
                closeContractAddress,
                fee,
                isFeeInSwap
            );
        } 
        else if(ETH_ADDRESS == closeContractAddress)
        {
            _openERC20ToEther(
                openValue,
                openContractAddress,
                closeTrader,
                closeValue,
                fee,
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );
        }
        else
        {
            _openERC20ToERC20(
                openValue,
                openContractAddress,
                closeValue,
                closeTrader,
                closeContractAddress,
                fee,
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );
        }
    }

    function _openEtherToERC20(
        uint256 ethValue,
        uint256 erc20Value,
        address payable erc20Trader,
        address erc20ContractAddress,
        uint256 fee,
        bool isFeeInSwap
    )
    private
    whenNotPaused
    onlyContract(erc20ContractAddress)
    {
        require(ethValue > 0, "[Validation] The ETH amount has to be larger than 0");
        require(erc20Value > 0, "[Validation] The ERC-20 amount has to be larger than 0");
        require(fee > 0, "[Validation] The fee has to be larger than 0");

        //Transferring fee to the wallet
        if(isFeeInSwap){
            require(msg.value >= ethValue, "[Validation] Enough ETH not sent");
            uint256 minRequiredFeeInSwap = getFeeInSwapForETH(ethValue);
            uint256 feeDiff = 0;
            if( fee < minRequiredFeeInSwap ) {
                feeDiff = minRequiredFeeInSwap.sub(fee);
                uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                //will allow if diff is less than 5%
                require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
            }
            _distributeFees(minRequiredFeeInSwap);
        }
        else {
            uint256 minRequiredFeeInEth = getFeeInEthForEth(ethValue);
            require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
            require(msg.value >= ethValue.add(minRequiredFeeInEth), "[Validation] Enough ETH not sent");
            (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
            require(success, "[Validation] Transfer of fee failed");
        }

        _swapId = _swapId.add(1);

        // Store the details of the swap.
        _swaps[_swapId] = Swap({
            openValue: ethValue,
            openTrader: msg.sender,
            openContractAddress: ETH_ADDRESS,
            closeValue: erc20Value,
            closeTrader: erc20Trader,
            closeContractAddress: erc20ContractAddress,
            swapType: SwapType.ETH_TO_ERC20,
            status: Status.OPEN
        });

        emit Open(_swapId, msg.sender, erc20Trader);
    }

    function _openERC20ToEther(
        uint256 erc20Value,
        address erc20ContractAddress,
        address payable ethTrader,
        uint256 ethValue,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    whenNotPaused
    onlyContract(erc20ContractAddress)
    {
        require(ethValue > 0, "[Validation] The ETH amount has to be larger than 0");
        require(erc20Value > 0, "[Validation] The ERC-20 amount has to be larger than 0");

        if(!isFreeToken(erc20ContractAddress)) {
            //Transfer fee to the wallet
            if(isFeeInSwap){
                uint256 minRequiredFeeInSwap = getFeeInSwapForERC20(erc20Value, erc20ContractAddress, calcFeeUsingTotalSupply);
                uint256 feeDiff = 0;
                if( fee < minRequiredFeeInSwap ) {
                    feeDiff = minRequiredFeeInSwap.sub(fee);
                    uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                    //will allow if diff is less than 5%
                    require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
                }
                _distributeFees(minRequiredFeeInSwap);
            }
            else {
                uint256 minRequiredFeeInEth = calcFeeUsingTotalSupply ? 
                    getFeeInEthForERC20UsingTotalSupply(erc20Value, erc20ContractAddress) : 
                    getFeeInEthForERC20(erc20Value, erc20ContractAddress);
                require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
                require(msg.value >= minRequiredFeeInEth, "[Validation] msg.value doesn't contain enough ETH for fee");
                (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
                require(success, "[Validation] Transfer of fee failed");
            }
        }
        // Transfer value from the opening trader to this contract.
        IERC20 openERC20Contract = IERC20(erc20ContractAddress);
        require(erc20Value <= openERC20Contract.allowance(msg.sender, address(this)));
        require(openERC20Contract.transferFrom(msg.sender, address(this), erc20Value));

        _swapId = _swapId.add(1);

        // Store the details of the swap.
        _swaps[_swapId] = Swap({
            openValue: erc20Value,
            openTrader: msg.sender,
            openContractAddress: erc20ContractAddress,
            closeValue: ethValue,
            closeTrader: ethTrader,
            closeContractAddress: ETH_ADDRESS,
            swapType: SwapType.ERC20_TO_ETH,
            status: Status.OPEN
        });

        emit Open(_swapId, msg.sender, ethTrader);
    }

    function _openERC20ToERC20(
        uint256 openValue,
        address openContractAddress,
        uint256 closeValue,
        address payable closeTrader,
        address closeContractAddress,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    whenNotPaused
    {
        require(openValue > 0, "[Validation] The open ERC-20 amount has to be larger than 0");
        require(closeValue > 0, "[Validation] The close ERC-20 amount has to be larger than 0");

        if(!isFreeToken(openContractAddress)) {
            //Transfer fee to the wallet
            if(isFeeInSwap){
                uint256 minRequiredFeeInSwap = getFeeInSwapForERC20(openValue, openContractAddress, calcFeeUsingTotalSupply);
                uint256 feeDiff = 0;
                if( fee < minRequiredFeeInSwap ) {
                    feeDiff = minRequiredFeeInSwap.sub(fee);
                    uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                    //will allow if diff is less than 5%
                    require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
                }
                _distributeFees(minRequiredFeeInSwap);
            }
            else {
                uint256 minRequiredFeeInEth = calcFeeUsingTotalSupply ? 
                    getFeeInEthForERC20UsingTotalSupply(openValue, openContractAddress) : 
                    getFeeInEthForERC20(openValue, openContractAddress);
                require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
                require(msg.value >= minRequiredFeeInEth, "[Validation] msg.value doesn't contain enough ETH for fee");
                (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
                require(success, "[Validation] Transfer of fee failed");
            }
        }

        // Transfer value from the opening trader to this contract.
        IERC20 openERC20Contract = IERC20(openContractAddress);
        require(openValue <= openERC20Contract.allowance(msg.sender, address(this)));
        require(openERC20Contract.transferFrom(msg.sender, address(this), openValue));

        _swapId = _swapId.add(1);

        // Store the details of the swap.
        _swaps[_swapId] = Swap({
            openValue: openValue,
            openTrader: msg.sender,
            openContractAddress: openContractAddress,
            closeValue: closeValue,
            closeTrader: closeTrader,
            closeContractAddress: closeContractAddress,
            swapType: SwapType.ERC20_TO_ERC20,
            status: Status.OPEN
        });

        emit Open(_swapId, msg.sender, closeTrader);
    }

    function close(
        uint256 id,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    external
    payable
    onlyOpenSwaps(id)
    {
        Swap memory swap = _swaps[id];
        require(swap.closeTrader == _msgSender(), "[Validation]: The caller is not authorized to close the trade");
        if(SwapType.ETH_TO_ERC20 == swap.swapType)
        {
            _closeEtherToERC20(
                id,
                fee,
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );
        } 
        else if(SwapType.ERC20_TO_ETH == swap.swapType)
        {
            _closeERC20ToEther(
                id,
                fee,
                isFeeInSwap
            );
        }
        else
        {
            _closeERC20ToERC20(
                id,
                fee,
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );
        }
    }

    function _closeEtherToERC20(
        uint256 id,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    onlyOpenSwaps(id)
    {
        Swap storage swap = _swaps[id];

        if(!isFreeToken(swap.closeContractAddress)) {
            //Transfer fee to the wallet
            if(isFeeInSwap){
                uint256 minRequiredFeeInSwap = getFeeInSwapForERC20(swap.closeValue, swap.closeContractAddress, calcFeeUsingTotalSupply);
                uint256 feeDiff = 0;
                if( fee < minRequiredFeeInSwap ) {
                    feeDiff = minRequiredFeeInSwap.sub(fee);
                    uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                    //will allow if diff is less than 5%
                    require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
                }
                _distributeFees(minRequiredFeeInSwap);
            }
            else {
                uint256 minRequiredFeeInEth = calcFeeUsingTotalSupply ? 
                    getFeeInEthForERC20UsingTotalSupply(swap.closeValue, swap.closeContractAddress) : 
                    getFeeInEthForERC20(swap.closeValue, swap.closeContractAddress);
                require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
                require(msg.value >= minRequiredFeeInEth, "[Validation] msg.value doesn't contain enough ETH for fee");
                (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
                require(success, "[Validation] Transfer of fee failed");
            }
        }
        // Close the swap.
        swap.status = Status.CLOSED;

        // Transfer the ERC20 funds from the ERC20 trader to the ETH trader.
        IERC20 erc20Contract = IERC20(swap.closeContractAddress);
        require(swap.closeValue <= erc20Contract.allowance(swap.closeTrader, address(this)));
        require(erc20Contract.transferFrom(swap.closeTrader, swap.openTrader, swap.closeValue));

        // Transfer the ETH funds from this contract to the ERC20 trader.
        swap.closeTrader.transfer(swap.openValue);

        emit Close(id);
    }

    function _closeERC20ToEther(
        uint256 id,
        uint256 fee,
        bool isFeeInSwap
    )
    private
    onlyOpenSwaps(id)
    {
        Swap storage swap = _swaps[id];

        //Transferring fee to the wallet
        if(isFeeInSwap){
            require(msg.value >= swap.closeValue, "[Validation] Enough ETH not sent");
            uint256 minRequiredFeeInSwap = getFeeInSwapForETH(swap.closeValue);
            uint256 feeDiff = 0;
            if( fee < minRequiredFeeInSwap ) {
                feeDiff = minRequiredFeeInSwap.sub(fee);
                uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                //will allow if diff is less than 5%
                require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
            }
            _distributeFees(minRequiredFeeInSwap);
        }
        else {
            uint256 minRequiredFeeInEth = getFeeInEthForEth(swap.closeValue);
            require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
            require(msg.value >= swap.closeValue.add(minRequiredFeeInEth), "[Validation] Enough ETH not sent");
            (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
            require(success, "[Validation] Transfer of fee failed");
        }

        // Close the swap.
        swap.status = Status.CLOSED;

        // Transfer the opening funds from this contract to the eth trader.
        IERC20 openERC20Contract = IERC20(swap.openContractAddress);
        require(openERC20Contract.transfer(swap.closeTrader, swap.openValue));

        (bool success,) = swap.openTrader.call.value(swap.closeValue)("");
        require(success, "[Validation] Transfer of eth failed");

        emit Close(id);
    }

    function _closeERC20ToERC20(
        uint256 id,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    onlyOpenSwaps(id)
    {
        Swap storage swap = _swaps[id];

        if(!isFreeToken(swap.closeContractAddress)) {
            //Transfer fee to the wallet
            if(isFeeInSwap){
                uint256 minRequiredFeeInSwap = getFeeInSwapForERC20(swap.closeValue, swap.closeContractAddress, calcFeeUsingTotalSupply);
                uint256 feeDiff = 0;
                if( fee < minRequiredFeeInSwap ) {
                    feeDiff = minRequiredFeeInSwap.sub(fee);
                    uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                    //will allow if diff is less than 5%
                    require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
                }
                _distributeFees(minRequiredFeeInSwap);
            }
            else {
                uint256 minRequiredFeeInEth = calcFeeUsingTotalSupply ? 
                    getFeeInEthForERC20UsingTotalSupply(swap.closeValue, swap.closeContractAddress) : 
                    getFeeInEthForERC20(swap.closeValue, swap.closeContractAddress);
                require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
                require(msg.value >= minRequiredFeeInEth, "[Validation] msg.value doesn't contain enough ETH for fee");
                (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
                require(success, "[Validation] Transfer of fee failed");
            }
        }

        // Close the swap.
        swap.status = Status.CLOSED;

        // Transfer the closing funds from the closing trader to the opening trader.
        IERC20 closeERC20Contract = IERC20(swap.closeContractAddress);
        require(swap.closeValue <= closeERC20Contract.allowance(swap.closeTrader, address(this)));
        require(closeERC20Contract.transferFrom(swap.closeTrader, swap.openTrader, swap.closeValue));

        // Transfer the opening funds from this contract to the closing trader.
        IERC20 openERC20Contract = IERC20(swap.openContractAddress);
        require(openERC20Contract.transfer(swap.closeTrader, swap.openValue));

        emit Close(id);
    }

    function cancel(uint256 id)
    external
    onlyOpenSwaps(id)
    {
        Swap memory swap = _swaps[id];
        require(swap.openTrader == _msgSender(), "[Validation]: The caller is not authorized to cancel the trade");
        if(SwapType.ETH_TO_ERC20 == swap.swapType) {
            _cancelEtherToERC20(id);
        }
        else {
            _cancelERC20(id);
        }
    }

    function _cancelEtherToERC20(uint256 id)
    private
    onlyOpenSwaps(id)
    {
        // Cancel the swap.
        Swap storage swap = _swaps[id];
        swap.status = Status.CANCELLED;

        // Transfer the ETH value from this contract back to the ETH trader.
        swap.openTrader.transfer(swap.openValue);
        emit Cancel(id);
    }

    function _cancelERC20(uint256 id)
    private
    onlyOpenSwaps(id)
    {
        // Cancel the swap.
        Swap storage swap = _swaps[id];
        swap.status = Status.CANCELLED;

        // Transfer opening value from this contract back to the opening trader.
        IERC20 openERC20Contract = IERC20(swap.openContractAddress);
        require(openERC20Contract.transfer(swap.openTrader, swap.openValue));

        emit Cancel(id);
    }

    function check(uint256 id)
    external
    view
    returns (
        uint256 openValue,
        address openTrader,
        address openContractAddress,
        uint256 closeValue,
        address closeTrader,
        address closeContractAddress,
        SwapType swapType,
        Status status
    )
    {
        Swap memory swap = _swaps[id];
        return (
            swap.openValue, 
            swap.openTrader, 
            swap.openContractAddress, 
            swap.closeValue, 
            swap.closeTrader, 
            swap.closeContractAddress,
            swap.swapType,
            swap.status
        );
    }

    /**
    * @dev called by admin to add given token to free tokens list
    */
    function addTokenToFreeList(address token)
    external
    onlyOwner
    onlyContract(token)
    {
        _listFreeTokens[token] = true;
    }

    /**
    * @dev called by admin to remove given token from free tokens list
    */
    function removeTokenFromFreeList(address token)
    external
    onlyOwner
    onlyContract(token)
    {
        _listFreeTokens[token] = false;
    }

    /**
    * @dev Checks if token is in free list
    * @param token The address to check
    */
    function isFreeToken(address token)
    public
    view
    returns(bool)
    {
        return _listFreeTokens[token];
    }
}

contracts/SwapSmartLock.sol.content:
pragma solidity 0.6.2;

import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "./IERC20Extended.sol";
import "./IPriceEstimator.sol";

/**
* @dev This contract will hold user scheduled payments which will be released after
* release time
*/
contract SwapSmartLock is Initializable, OwnableUpgradeSafe, PausableUpgradeSafe {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using Address for address;

    enum Status { CLOSED, OPEN }

    IERC20 private _swapToken;

    //Wallet where fees will go
    address payable private _feesWallet;

    //Wallet where dev fund will go
    address private _devWallet;

    address constant private ETH_ADDRESS = address(
        0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE
    );

    uint256 constant private DEV_FEE_PERCENTAGE = 10;
    uint256 constant private BURN_FEE_PERCENTAGE = 10;

    struct Payment {
        address token;// Token address
        address sender;
        address payable beneficiary;// Beneficary who will receive funds
        uint256 amount;
        uint256 releaseTime;
        uint256 createdAt;
        Status status;
    }

    //Global payment id. Also give total number of payments made so far
    uint256 private _paymentId;

    //list of all payment ids for a user/beneficiary
    mapping(address => uint256[]) private _beneficiaryVsPaymentIds;

    //list of all payment ids for a sender
    mapping(address => uint256[]) private _senderVsPaymentIds;

    mapping(uint256 => Payment) private _idVsPayment;

    IPriceEstimator private _priceEstimator;

    uint256 private _ethFeePercentage;
    uint256 private _allowedFeeSlippagePercentage;
    uint256 private _uniswapFeePercentage;

    uint256 private _maxFeeInEth;

    //list of free tokens
    mapping(address => bool) private _listFreeTokens;

    event PaymentScheduled(
        address indexed token,
        address indexed sender,
        address indexed beneficiary,
        uint256 id,
        uint256 amount,
        uint256 releaseTime,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    );

    event PaymentReleased(
        uint256 indexed id,
        address indexed beneficiary,
        address indexed token
    );

    event FeeWalletChanged(address indexed wallet);
    event DevWalletChanged(address indexed wallet);
    event SwapTokenUpdated(address indexed swapTokenAddress);

    modifier onlyContract(address account)
    {
        require(account.isContract(), "[Validation] The address does not contain a contract");
        _;
    }

    modifier canRelease(uint256 id) {
        require(releasable(id), "[Release]: Can't release payment");
        _;
    }

    /**
    * @dev initialize
    * @param swapTokenAddress Address of the swap token
    * @param feesWallet Wallet address where fees will go
    * @param devWallet Wallet address where dev fund will go
    */
    function initialize(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    external
    onlyContract(swapTokenAddress)
    onlyContract(priceEstimator)
    {
        __SwapSmartLock_init(swapTokenAddress, feesWallet, devWallet, priceEstimator);
    }

    function __SwapSmartLock_init(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    internal
    initializer
    {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Pausable_init_unchained();
        __SwapSmartLock_init_unchained(swapTokenAddress, feesWallet, devWallet, priceEstimator);
    }

    function __SwapSmartLock_init_unchained(
        address swapTokenAddress,
        address payable feesWallet,
        address devWallet,
        address priceEstimator
    )
    internal
    initializer
    {
        require(feesWallet != address(0), "[Validation] feesWallet is the zero address");
        require(devWallet != address(0), "[Validation] devWallet is the zero address");

        _swapToken = IERC20(swapTokenAddress);
        _feesWallet = feesWallet;
        _devWallet = devWallet;
        _priceEstimator = IPriceEstimator(priceEstimator);
        _ethFeePercentage = 1;
        _allowedFeeSlippagePercentage = 5;
        _uniswapFeePercentage = 3;
    }

    /**
    * @dev returns the fee receiver wallet address
    */
    function getFeesWallet()
    external
    view
    returns(address)
    {
        return _feesWallet;
    }

    /**
    * @dev returns the dev fund wallet address
    */
    function getDevWallet()
    external
    view
    returns(address)
    {
        return _devWallet;
    }

    /**
    * @dev Returns swap token address
    */
    function getSwapToken()
    external
    view
    returns(address)
    {
        return address(_swapToken);
    }

    /**
    * @dev Returns price estimator address
    */
    function getPriceEstimator()
    external
    view
    returns(address)
    {
        return address(_priceEstimator);
    }

    /**
    * @dev Returns information about payment details
    * @param id Payment id
    */
    function getPaymentDetails(uint256 id) external view returns(
        address token,
        uint256 amount,
        uint256 releaseTime,
        address sender,
        address beneficiary,
        uint256 createdAt,
        Status status
    )
    {
        require(_idVsPayment[id].amount != 0, "[Validation] Payment details not found");

        Payment memory payment = _idVsPayment[id];
        token = payment.token;
        amount = payment.amount;
        releaseTime = payment.releaseTime;
        sender = payment.sender;
        beneficiary = payment.beneficiary;
        createdAt = payment.createdAt;
        status = payment.status;

        return(
            token,
            amount,
            releaseTime,
            sender,
            beneficiary,
            createdAt,
            status
        );
    }

    /**
    * @dev Returns all payment ids for beneficiary
    * @param user Address of the user
    */
    function getBeneficiaryPaymentIds(address user)
    external 
    view 
    returns (uint256[] memory ids)
    {
        return _beneficiaryVsPaymentIds[user];
    }

    /**
    * @dev Returns all payment ids for sender
    * @param user Address of the user
    */
    function getSenderPaymentIds(address user)
    external
    view
    returns (uint256[] memory ids)
    {
        return _senderVsPaymentIds[user];
    }

    /**
    * @dev Update swap token address
    * @param swapTokenAddress New swap token address
    */
    function setSwapToken(address swapTokenAddress)
    external
    onlyOwner
    onlyContract(swapTokenAddress)
    {
        require(
            swapTokenAddress != address(0),
            "[Validation]: Invalid swap token address"
        );
        _swapToken = IERC20(swapTokenAddress);
        emit SwapTokenUpdated(swapTokenAddress);
    }

    /**
    * @dev Allows admin to set fee receiver wallet
    * @param wallet New wallet address
    */
    function setFeeWallet(address payable wallet)
    external
    onlyOwner
    {
        require(
            wallet != address(0),
            "[Validation] feesWallet is the zero address"
        );
        _feesWallet = wallet;

        emit FeeWalletChanged(wallet);
    }

    /**
    * @dev Allows admin to set fee receiver wallet
    * @param wallet New wallet address
    */
    function setDevWallet(address payable wallet)
    external
    onlyOwner
    {
        require(
            wallet != address(0),
            "[Validation] devWallet is the zero address"
        );
        _devWallet = wallet;

        emit DevWalletChanged(wallet);
    }

    /**
    * @dev Update price estimator address
    * @param priceEstimator New price estimator address
    */
    function setPriceEstimator(address priceEstimator)
    external
    onlyOwner
    onlyContract(priceEstimator)
    {
        require(
            priceEstimator != address(0),
            "[Validation]: Invalid price estimator address"
        );
        _priceEstimator = IPriceEstimator(priceEstimator);
    }

    /**
    * @dev Update fees
    * @param ethFeePercentage New percentage of fee in eth
    */
    function setEthFeePercentage(uint8 ethFeePercentage)
    external
    onlyOwner
    {
        require(
            ethFeePercentage >= 0 && ethFeePercentage <= 100,
            "[Validation]: ETH Fee percentage must be between 0 to 100"
        );
        _ethFeePercentage = ethFeePercentage;
    }

    /**
    * @dev Update fee slippage percentage allowance for erc20
    * @param allowedFeeSlippagePercentage New allowed fee slippage percentage for fee in erc20
    */
    function setAllowedFeeSlippagePercentage(uint8 allowedFeeSlippagePercentage)
    external
    onlyOwner
    {
        require(
            allowedFeeSlippagePercentage >= 0 && allowedFeeSlippagePercentage <= 100,
            "[Validation]: Allowed Fee Slippage percentage must be between 0 to 100"
        );
        _allowedFeeSlippagePercentage = allowedFeeSlippagePercentage;
    }

    /**
    * @dev Update Uniswap fees
    * @param uniswapFeePercentage New percentage of uniswap fee
    */
    function setUniswapFeePercentage(uint8 uniswapFeePercentage)
    external
    onlyOwner
    {
        require(
            uniswapFeePercentage >= 0 && uniswapFeePercentage <= 100,
            "[Validation]: Uniswap Fee percentage must be between 0 to 100"
        );
        _uniswapFeePercentage = uniswapFeePercentage;
    }

    /**
    * @dev Sets maximum fee in ETH
    * @param ethAmountInWei Maximum fee amount in wei
    */
    function setMaxFeeInEth(uint256 ethAmountInWei)
    external
    onlyOwner
    {
        _maxFeeInEth = ethAmountInWei;
    }

    function getFeeInEthForEth(uint256 amount)
    public
    view
    returns (uint256) 
    {
        return Math.min(amount.mul(_ethFeePercentage).div(100), _maxFeeInEth); //1% of ETH amount
    }

    function getFeeInEthForERC20(uint256 amount, address token)
    public 
    view
    returns (uint256)
    {
        if(isFreeToken(token)) {
            return 0;
        } else {
            //price should be estimated by 1 token because Uniswap algo changes price based on large amount
            uint256 tokenBits = 10 ** uint256(IERC20Extended(token).decimals());
            uint256 estFeesInEthPerUnit = _priceEstimator.getEstimatedETHforERC20(tokenBits, token)[0];
            //subtract uniswap 0.30% fees
            //_uniswapFeePercentage is a percentage expressed in 1/10 (a tenth) of a percent hence we divide by 1000
            estFeesInEthPerUnit = estFeesInEthPerUnit.sub(estFeesInEthPerUnit.mul(_uniswapFeePercentage).div(1000));
            uint256 equivEth = amount.mul(estFeesInEthPerUnit).div(tokenBits); //multiply by amount to be scheduled amount
            return getFeeInEthForEth(equivEth);
        }
    }

    function getFeeInEthForERC20UsingTotalSupply(uint256 amount, address token)
    public
    view
    returns (uint256)
    {
        if(isFreeToken(token)) {
            return 0;
        } else {
            //per 1% supply , 0.1 ETH is the fee
            uint256 tokenTotalSupply = IERC20(token).totalSupply();
            uint256 percentage = amount.mul(tokenTotalSupply).mul(100).div(tokenTotalSupply);
            uint256 ethFeeInWei = 100000000000000000; //0.1 ETH
            return Math.min(percentage.mul(ethFeeInWei).div(tokenTotalSupply), _maxFeeInEth);
        }
    }

    function getFeeInSwapForETH(uint256 amount)
    public
    view
    returns (uint256)
    {
        uint256 feesInEth = getFeeInEthForEth(amount);
        return _getEquivSwapFee(feesInEth);
    }

    function getFeeInSwapForERC20(uint256 amount, address token, bool calcFeeUsingTotalSupply)
    public
    view
    returns (uint256)
    {
        uint256 feesInEth = calcFeeUsingTotalSupply ? getFeeInEthForERC20UsingTotalSupply(amount, token) : getFeeInEthForERC20(amount, token);
        return _getEquivSwapFee(feesInEth);
    }

    function _getEquivSwapFee(uint256 feesInEth)
    private
    view
    returns (uint256)
    {
        uint256 feesInEthIfPaidViaSwap = feesInEth.div(2);
        uint256 swapPerEth = _priceEstimator.getEstimatedERC20forETH(1, address(_swapToken))[0];
        //subtract uniswap 0.30% fees
        //_uniswapFeePercentage is a percentage expressed in 1/10 (a tenth) of a percent hence we divide by 1000
        uint256 estSwapPerEth = swapPerEth.sub(swapPerEth.mul(_uniswapFeePercentage).div(1000));
        return feesInEthIfPaidViaSwap.mul(estSwapPerEth);
    }

    /**
    * @dev Allows user to schedule payment. In case of ERC-20 token the user will
    * first have to approve the contract to spend on his/her behalf
    * @param tokenAddress Address of the token to be paid
    * @param amount Amount of tokens to pay
    * @param releaseTime release time after which tokens to be released. In seconds
    * @param beneficiary Address of the beneficiary
    * @param isFeeInSwap Bool to check if fee to be paid in swap token or not
    */
    function schedulePayment(
        address tokenAddress,
        uint256 amount,
        uint256 releaseTime,
        address payable beneficiary,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    external
    payable
    whenNotPaused
    {
        _schedulePayment(
            tokenAddress,
            amount,
            releaseTime,
            beneficiary,
            msg.value,
            fee,
            isFeeInSwap,
            calcFeeUsingTotalSupply
        );
    }

    /**
    * @dev Helper method to schedule payment
    */
    function _schedulePayment(
        address tokenAddress,
        uint256 amount,
        uint256 releaseTime,
        address payable beneficiary,
        uint256 value,
        uint256 fee,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    returns(uint256)
    {
        require(amount > 0, "[Validation] The amount has to be larger than 0");
        if(!isFreeToken(tokenAddress)) {
            require(fee > 0, "[Validation] The fee has to be larger than 0");
        }
        require(beneficiary != address(0), "[Validation] Invalid beneficiary address");

        uint256 remValue = value;

        if(ETH_ADDRESS == tokenAddress) {
            _scheduleETH(
                amount,
                fee,
                releaseTime,
                beneficiary,
                value,
                isFeeInSwap
            );

            if(isFeeInSwap) {
                remValue = remValue.sub(amount);
            } else {
                remValue = remValue.sub(amount).sub(fee);
            }
        }
        else {
            _scheduleERC20(
                tokenAddress,
                amount,
                fee,
                releaseTime,
                beneficiary,
                value,
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );

            if(!isFeeInSwap) {
                remValue = remValue.sub(fee);
            }
        }

        emit PaymentScheduled(
            tokenAddress,
            msg.sender,
            beneficiary,
            _paymentId,
            amount,
            releaseTime,
            fee,
            isFeeInSwap,
            calcFeeUsingTotalSupply
        );

        return remValue;
    }

    /**
    * @dev Helper method to schedule payment in ETH
    */
    function _scheduleETH(
        uint256 amount,
        uint256 fee,
        uint256 releaseTime,
        address payable beneficiary,
        uint256 value,
        bool isFeeInSwap
    )
    private
    {
        //Transferring fee to the wallet
        if(isFeeInSwap){
            require(value >= amount, "[Validation] Enough ETH not sent");
            uint256 minRequiredFeeInSwap = getFeeInSwapForETH(amount);
            uint256 feeDiff = 0;
            if( fee < minRequiredFeeInSwap ) {
                feeDiff = minRequiredFeeInSwap.sub(fee);
                uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                //will allow if diff is less than 5%
                require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
            }
            _distributeFees(minRequiredFeeInSwap);
        }
        else {
            uint256 minRequiredFeeInEth = getFeeInEthForEth(amount);
            require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
            require(value >= amount.add(minRequiredFeeInEth), "[Validation] Enough ETH not sent");
            (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
            require(success, "[Validation] Transfer of fee failed");
        }

        _paymentId = _paymentId.add(1);

        _idVsPayment[_paymentId] = Payment({
            token: ETH_ADDRESS,
            amount: amount,
            releaseTime: releaseTime,
            sender: msg.sender,
            beneficiary: beneficiary,
            createdAt: block.timestamp,
            status: Status.OPEN
        });
        _beneficiaryVsPaymentIds[beneficiary].push(_paymentId);
        _senderVsPaymentIds[msg.sender].push(_paymentId);
    }

    /**
    * @dev Helper method to schedule payment in ERC-20 tokens
    */
    function _scheduleERC20(
        address token,
        uint256 amount,
        uint256 fee,
        uint256 releaseTime,
        address payable beneficiary,
        uint256 value,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    private
    onlyContract(token)
    {
        if(!isFreeToken(token)) {
            //Transfer fee to the wallet
            if(isFeeInSwap){
                uint256 minRequiredFeeInSwap = getFeeInSwapForERC20(amount, token, calcFeeUsingTotalSupply);
                uint256 feeDiff = 0;
                if( fee < minRequiredFeeInSwap ) {
                    feeDiff = minRequiredFeeInSwap.sub(fee);
                    uint256 feeSlippagePercentage = feeDiff.mul(100).div(minRequiredFeeInSwap);
                    //will allow if diff is less than 5%
                    require(feeSlippagePercentage < _allowedFeeSlippagePercentage, "[Validation] Fee (SWAP) is below minimum required fee");
                }
                _distributeFees(minRequiredFeeInSwap);
            }
            else {
                uint256 minRequiredFeeInEth = calcFeeUsingTotalSupply ? getFeeInEthForERC20UsingTotalSupply(amount, token) : getFeeInEthForERC20(amount, token);
                require(fee >= minRequiredFeeInEth, "[Validation] Fee (ETH) is below minimum required fee");
                require(value >= minRequiredFeeInEth, "[Validation] msg.value doesn't contain enough ETH for fee");
                (bool success,) = _feesWallet.call.value(minRequiredFeeInEth)("");
                require(success, "[Validation] Transfer of fee failed");
            }
        }

        //Transfer required amount of tokens to the contract from user balance
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        _paymentId = _paymentId.add(1);

        _idVsPayment[_paymentId] = Payment({
            token: token,
            amount: amount,
            releaseTime: releaseTime,
            sender: msg.sender,
            beneficiary: beneficiary,
            createdAt: block.timestamp,
            status: Status.OPEN
        });
        _beneficiaryVsPaymentIds[beneficiary].push(_paymentId);
        _senderVsPaymentIds[msg.sender].push(_paymentId);
    }

    function _distributeFees(uint256 fee)
    private
    {
        uint256 devAmount = fee.mul(DEV_FEE_PERCENTAGE).div(100); //10%
        uint256 burnAmount = fee.mul(BURN_FEE_PERCENTAGE).div(100); //10%
        uint256 remAmount = fee.sub(devAmount).sub(burnAmount); //80%

        _swapToken.safeTransferFrom(msg.sender, _feesWallet, remAmount);
        _swapToken.safeTransferFrom(msg.sender, _devWallet, devAmount);
        IERC20Extended(address(_swapToken)).burnFrom(msg.sender, burnAmount);
    }

    /**
    * @dev Allows user to schedule payment. In case of ERC-20 token the user will
    * first have to approve the contract to spend on his/her behalf
    * @param tokenAddress Address of the token to be paid
    * @param amounts List of amount of tokens to pay
    * @param releaseTimes List of release times after which tokens to be released. In seconds
    * @param beneficiaries List of addresses of the beneficiaries
    * @param isFeeInSwap Bool to check if fee to be paid in swap token or not
    */
    function scheduleBulkPayment(
        address tokenAddress,
        uint256[] calldata amounts,
        uint256[] calldata releaseTimes,
        address payable[] calldata beneficiaries,
        uint256[] calldata fees,
        bool isFeeInSwap,
        bool calcFeeUsingTotalSupply
    )
    external
    payable
    whenNotPaused
    {
        uint256 remValue = msg.value;
        require(amounts.length == releaseTimes.length, "SwapSmartLock: Invalid input");
        require(amounts.length == beneficiaries.length, "SwapSmartLock: Invalid input");
        require(amounts.length == fees.length, "SwapSmartLock: Invalid input");

        for(uint256 i = 0; i < amounts.length; i++) {
            remValue = _schedulePayment(
                tokenAddress,
                amounts[i],
                releaseTimes[i],
                beneficiaries[i],
                remValue,
                fees[i],
                isFeeInSwap,
                calcFeeUsingTotalSupply
            );
        }
    }

    /**
    * @dev Allows beneficiary of payment to release payment after release time
    * @param id Id of the scheduled payment
    */
    function release(uint256 id)
    external
    canRelease(id)
    {
        Payment memory payment = _idVsPayment[id];
        if(ETH_ADDRESS == payment.token) {
            _releaseETH(id);
        }
        else {
            _releaseERC20(id);
        }

        emit PaymentReleased(
            id,
            payment.beneficiary,
            payment.token
        );
    }

    /**
    * @dev Returns whether given payment can be released or not
    * @param id id of a payment
    */
    function releasable(uint256 id)
    public
    view
    returns(bool)
    {
        Payment memory payment = _idVsPayment[id];
        if( (payment.status == Status.OPEN) && (payment.releaseTime <= block.timestamp) )
        {
            return true;
        }
        return false;
    }

    /**
    * @dev Helper method to release ETH
    */
    function _releaseETH(uint256 id)
    private
    {
        Payment storage payment = _idVsPayment[id];
        payment.status = Status.CLOSED;
        (bool success,) = payment.beneficiary.call.value(payment.amount)("");
        require(success, "[Release] Failed to transfer ETH");
    }

    /**
    * @dev Helper method to release ERC-20
    */
    function _releaseERC20(uint256 id)
    private
    {
        Payment storage payment = _idVsPayment[id];
        payment.status = Status.CLOSED;
        IERC20(payment.token).safeTransfer(payment.beneficiary, payment.amount);
    }

    /**
    * @dev Called by an admin to pause, triggers stopped state.
    */
    function pause()
    external
    onlyOwner 
    {
        _pause();
    }

    /**
    * @dev Called by an admin to unpause, returns to normal state.
    */
    function unpause()
    external
    onlyOwner
    {
        _unpause();
    }

    /**
    * @dev called by admin to add given token to free tokens list
    */
    function addTokenToFreeList(address token)
    external
    onlyOwner
    onlyContract(token)
    {
        _listFreeTokens[token] = true;
    }

    /**
    * @dev called by admin to remove given token from free tokens list
    */
    function removeTokenFromFreeList(address token)
    external
    onlyOwner
    onlyContract(token)
    {
        _listFreeTokens[token] = false;
    }

    /**
     * @dev Checks if token is in free list
     * @param token The address to check
    */
    function isFreeToken(address token)
    public
    view
    returns(bool)
    {
        return _listFreeTokens[token];
    }

}

contracts/SwapStakingContract.sol.content:
pragma solidity 0.6.2;

import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Address.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Arrays.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/access/AccessControl.sol";

contract SwapStakingContract is Initializable, ContextUpgradeSafe, AccessControlUpgradeSafe, PausableUpgradeSafe, ReentrancyGuardUpgradeSafe {

    using SafeMath for uint256;
    using Math for uint256;
    using Address for address;
    using Arrays for uint256[];

    bytes32 private constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 private constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 private constant REWARDS_DISTRIBUTOR_ROLE = keccak256("REWARDS_DISTRIBUTOR_ROLE");

    // EVENTS
    event StakeDeposited(address indexed account, uint256 amount);
    event WithdrawInitiated(address indexed account, uint256 amount, uint256 initiateDate);
    event WithdrawExecuted(address indexed account, uint256 amount, uint256 reward);
    event RewardsWithdrawn(address indexed account, uint256 reward);
    event RewardsDistributed(uint256 amount);

    // STRUCT DECLARATIONS
    struct StakeDeposit {
        uint256 amount;
        uint256 startDate;
        uint256 endDate;
        uint256 entryRewardPoints;
        uint256 exitRewardPoints;
        bool exists;
    }

    // STRUCT WITHDRAWAL
    struct WithdrawalState {
        uint256 initiateDate;
        uint256 amount;
    }

    // CONTRACT STATE VARIABLES
    IERC20 public token;
    address public rewardsAddress;
    uint256 public maxStakingAmount;
    uint256 public currentTotalStake;
    uint256 public unstakingPeriod;

    //reward calculations
    uint256 private totalRewardPoints;
    uint256 public rewardsDistributed;
    uint256 public rewardsWithdrawn;
    uint256 public totalRewardsDistributed;

    mapping(address => StakeDeposit) private _stakeDeposits;
    mapping(address => WithdrawalState) private _withdrawStates;

    // MODIFIERS
    modifier guardMaxStakingLimit(uint256 amount)
    {
        uint256 resultedStakedAmount = currentTotalStake.add(amount);
        require(resultedStakedAmount <= maxStakingAmount, "[Deposit] Your deposit would exceed the current staking limit");
        _;
    }

    modifier onlyContract(address account)
    {
        require(account.isContract(), "[Validation] The address does not contain a contract");
        _;
    }

    // PUBLIC FUNCTIONS
    function initialize(address _token, address _rewardsAddress, uint256 _maxStakingAmount, uint256 _unstakingPeriod)
    public
    onlyContract(_token)
    {
        __SwapStakingContract_init(_token, _rewardsAddress, _maxStakingAmount, _unstakingPeriod);
    }

    function __SwapStakingContract_init(address _token, address _rewardsAddress, uint256 _maxStakingAmount, uint256 _unstakingPeriod)
    internal
    initializer
    {
        require(
            _token != address(0),
            "[Validation] Invalid swap token address"
        );
        require(_maxStakingAmount > 0, "[Validation] _maxStakingAmount has to be larger than 0");
        __Context_init_unchained();
        __AccessControl_init_unchained();
        __Pausable_init_unchained();
        __ReentrancyGuard_init_unchained();
        __SwapStakingContract_init_unchained();

        pause();
        setRewardAddress(_rewardsAddress);
        unpause();

        token = IERC20(_token);
        maxStakingAmount = _maxStakingAmount;
        unstakingPeriod = _unstakingPeriod;
    }

    function __SwapStakingContract_init_unchained()
    internal
    initializer
    {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        _setupRole(OWNER_ROLE, _msgSender());
        _setupRole(REWARDS_DISTRIBUTOR_ROLE, _msgSender());
    }

    function pause()
    public
    {
        require(hasRole(PAUSER_ROLE, _msgSender()), "SwapStakingContract: must have pauser role to pause");
        _pause();
    }

    function unpause()
    public
    {
        require(hasRole(PAUSER_ROLE, _msgSender()), "SwapStakingContract: must have pauser role to unpause");
        _unpause();
    }

    function setRewardAddress(address _rewardsAddress)
    public
    whenPaused
    {
        require(hasRole(OWNER_ROLE, _msgSender()), "[Validation] The caller must have owner role to set rewards address");
        require(_rewardsAddress != address(0), "[Validation] _rewardsAddress is the zero address");
        require(_rewardsAddress != rewardsAddress, "[Validation] _rewardsAddress is already set to given address");
        rewardsAddress = _rewardsAddress;
    }

    function setTokenAddress(address _token)
    external
    onlyContract(_token)
    whenPaused
    {
        require(hasRole(OWNER_ROLE, _msgSender()), "[Validation] The caller must have owner role to set token address");
        require(
            _token != address(0),
            "[Validation] Invalid swap token address"
        );
        token = IERC20(_token);
    }

    function deposit(uint256 amount)
    external
    nonReentrant
    whenNotPaused
    {
        StakeDeposit storage stakeDeposit = _stakeDeposits[msg.sender];
        require(stakeDeposit.endDate == 0, "[Deposit] You have already initiated the withdrawal");

        uint256 oldPrincipal = stakeDeposit.amount;
        uint256 reward = _computeReward(stakeDeposit);
        uint256 newPrincipal = oldPrincipal.add(amount).add(reward);
        require(newPrincipal > oldPrincipal, "[Validation] The stake deposit has to be larger than 0");

        uint256 resultedStakedAmount = currentTotalStake.add(newPrincipal.sub(oldPrincipal));
        require(resultedStakedAmount <= maxStakingAmount, "[Deposit] Your deposit would exceed the current staking limit");

        stakeDeposit.amount = newPrincipal;
        stakeDeposit.startDate = block.timestamp;
        stakeDeposit.exists = true;
        stakeDeposit.entryRewardPoints = totalRewardPoints;

        currentTotalStake = resultedStakedAmount;

        // Transfer the Tokens to this contract
        require(token.transferFrom(msg.sender, address(this), amount), "[Deposit] Something went wrong during the token transfer");

        if( reward > 0 ) {
            //calculate withdrawed rewards in single distribution cycle
            rewardsWithdrawn = rewardsWithdrawn.add(reward);
            require(token.transferFrom(rewardsAddress, address(this), reward), "[Deposit] Something went wrong while transferring reward");
        }

        emit StakeDeposited(msg.sender, amount.add(reward));
    }

    function initiateWithdrawal(uint256 withdrawAmount)
    external
    nonReentrant
    whenNotPaused
    {
        StakeDeposit storage stakeDeposit = _stakeDeposits[msg.sender];
        WithdrawalState storage withdrawState = _withdrawStates[msg.sender];
        require(withdrawAmount > 0, "[Initiate Withdrawal] Invalid withdrawal amount");
        require(withdrawAmount <= stakeDeposit.amount, "[Initiate Withdrawal] Withdraw amount exceed the stake amount");
        require(stakeDeposit.exists && stakeDeposit.amount != 0, "[Initiate Withdrawal] There is no stake deposit for this account");
        require(stakeDeposit.endDate == 0, "[Initiate Withdrawal] You have already initiated the withdrawal");
        require(withdrawState.amount == 0, "[Initiate Withdrawal] You have already initiated the withdrawal");

        stakeDeposit.endDate = block.timestamp;
        stakeDeposit.exitRewardPoints = totalRewardPoints;
        withdrawState.amount = withdrawAmount;
        withdrawState.initiateDate = block.timestamp;

        currentTotalStake = currentTotalStake.sub(withdrawAmount);

        emit WithdrawInitiated(msg.sender, withdrawAmount, block.timestamp);
    }

    function executeWithdrawal()
    external
    nonReentrant
    whenNotPaused
    {
        StakeDeposit memory stakeDeposit = _stakeDeposits[msg.sender];
        WithdrawalState memory withdrawState = _withdrawStates[msg.sender];

        require(stakeDeposit.endDate != 0 || withdrawState.amount != 0, "[Withdraw] Withdraw amount is not initialized");
        require(stakeDeposit.exists && stakeDeposit.amount != 0, "[Withdraw] There is no stake deposit for this account");

        // validate enough days have passed from initiating the withdrawal
        uint256 daysPassed = (block.timestamp - stakeDeposit.endDate) / 1 days;
        require(unstakingPeriod <= daysPassed, "[Withdraw] The unstaking period did not pass");

        uint256 amount = withdrawState.amount != 0 ? withdrawState.amount : stakeDeposit.amount;
        uint256 reward = _computeReward(stakeDeposit);

        require(stakeDeposit.amount >= amount, "[withdraw] Remaining stakedeposit amount must be higher than withdraw amount");
        if (stakeDeposit.amount > amount) {
            _stakeDeposits[msg.sender].amount = _stakeDeposits[msg.sender].amount.sub(amount);
            _stakeDeposits[msg.sender].endDate = 0;
            _stakeDeposits[msg.sender].entryRewardPoints = totalRewardPoints;
        }
        else {
            delete _stakeDeposits[msg.sender];
        }

        require(token.transfer(msg.sender, amount), "[Withdraw] Something went wrong while transferring your initial deposit");

        if( reward > 0 ) {
            //calculate withdrawed rewards in single distribution cycle
            rewardsWithdrawn = rewardsWithdrawn.add(reward);
            require(token.transferFrom(rewardsAddress, msg.sender, reward), "[Withdraw] Something went wrong while transferring your reward");
        }

        _withdrawStates[msg.sender].amount = 0;
        _withdrawStates[msg.sender].initiateDate = 0;

        emit WithdrawExecuted(msg.sender, amount, reward);
    }

    function withdrawRewards()
    external
    nonReentrant
    whenNotPaused
    {
        StakeDeposit storage stakeDeposit = _stakeDeposits[msg.sender];
        require(stakeDeposit.exists && stakeDeposit.amount != 0, "[Rewards Withdrawal] There is no stake deposit for this account");
        require(stakeDeposit.endDate == 0, "[Rewards Withdrawal] You already initiated the full withdrawal");

        uint256 reward = _computeReward(stakeDeposit);

        require(reward > 0, "[Rewards Withdrawal] The reward amount has to be larger than 0");

        stakeDeposit.entryRewardPoints = totalRewardPoints;

        //calculate withdrawed rewards in single distribution cycle
        rewardsWithdrawn = rewardsWithdrawn.add(reward);

        require(token.transferFrom(rewardsAddress, msg.sender, reward), "[Rewards Withdrawal] Something went wrong while transferring your reward");

        emit RewardsWithdrawn(msg.sender, reward);
    }

    // VIEW FUNCTIONS FOR HELPING THE USER AND CLIENT INTERFACE
    function getStakeDetails(address account)
    external
    view
    returns (uint256 initialDeposit, uint256 startDate, uint256 endDate, uint256 rewards)
    {
        require(_stakeDeposits[account].exists && _stakeDeposits[account].amount != 0, "[Validation] This account doesn't have a stake deposit");

        StakeDeposit memory s = _stakeDeposits[account];

        return (s.amount, s.startDate, s.endDate, _computeReward(s));
    }

    function _computeReward(StakeDeposit memory stakeDeposit)
    private
    view
    returns (uint256)
    {
        uint256 rewardsPoints = 0;

        if ( stakeDeposit.endDate == 0 )
        {
            rewardsPoints = totalRewardPoints.sub(stakeDeposit.entryRewardPoints);
        }
        else
        {
            //withdrawal is initiated
            rewardsPoints = stakeDeposit.exitRewardPoints.sub(stakeDeposit.entryRewardPoints);
        }
        return stakeDeposit.amount.mul(rewardsPoints).div(10 ** 18);
    }

    function distributeRewards()
    external
    nonReentrant
    whenNotPaused
    {
        require(hasRole(REWARDS_DISTRIBUTOR_ROLE, _msgSender()),
            "[Validation] The caller must have rewards distributor role");
        _distributeRewards();
    }

    function _distributeRewards()
    private
    whenNotPaused
    {
        require(hasRole(REWARDS_DISTRIBUTOR_ROLE, _msgSender()),
            "[Validation] The caller must have rewards distributor role");
        require(currentTotalStake > 0, "[Validation] not enough total stake accumulated");
        uint256 rewardPoolBalance = token.balanceOf(rewardsAddress);
        require(rewardPoolBalance > 0, "[Validation] not enough rewards accumulated");

        uint256 newlyAdded = rewardPoolBalance.add(rewardsWithdrawn).sub(rewardsDistributed);
        uint256 ratio = newlyAdded.mul(10 ** 18).div(currentTotalStake);
        totalRewardPoints = totalRewardPoints.add(ratio);
        rewardsDistributed = rewardPoolBalance;
        rewardsWithdrawn = 0;
        totalRewardsDistributed = totalRewardsDistributed.add(newlyAdded);

        emit RewardsDistributed(newlyAdded);
    }

    function version() public pure returns (string memory) {
        return "v2";
    }
}

contracts/SwapToken.sol.content:
pragma solidity ^0.6.0;

import "@openzeppelin/contracts-ethereum-package/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20Pausable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20Burnable.sol";

/**
 * @dev {ERC20} token, including:
 *
 *  - ability for holders to burn (destroy) their tokens
 *  - a minter role that allows for token minting (creation)
 *  - a pauser role that allows to stop all token transfers
 *
 * This contract uses {AccessControl} to lock permissioned functions using the
 * different roles - head to its documentation for details.
 *
 * The account that deploys the contract will be granted the minter and pauser
 * roles, as well as the default admin role, which will let it grant both minter
 * and pauser roles to aother accounts
 */
contract SwapToken is Initializable, ContextUpgradeSafe, AccessControlUpgradeSafe, ERC20BurnableUpgradeSafe, ERC20PausableUpgradeSafe {
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE` and `PAUSER_ROLE` to the
     * account that deploys the contract.
     *
     * See {ERC20-constructor}.
     */

    function initialize(string memory name, string memory symbol, uint8 decimals, uint256 totalSupply) public {
        __SwapToken_init(name, symbol, decimals, totalSupply);
    }

    function __SwapToken_init(string memory name, string memory symbol, uint8 decimals, uint256 totalSupply) internal initializer {
        __Context_init_unchained();
        __AccessControl_init_unchained();
        __ERC20_init_unchained(name, symbol);
        __ERC20Burnable_init_unchained();
        __Pausable_init_unchained();
        __ERC20Pausable_init_unchained();
        __SwapToken_init_unchained();
        _mint(_msgSender(), totalSupply * (10 ** uint256(decimals)));
    }

    function __SwapToken_init_unchained() internal initializer {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
    }

    /**
     * @dev Pauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_pause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function pause() public {
        require(hasRole(PAUSER_ROLE, _msgSender()), "SwapToken: must have pauser role to pause");
        _pause();
    }

    /**
     * @dev Unpauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_unpause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function unpause() public {
        require(hasRole(PAUSER_ROLE, _msgSender()), "SwapToken: must have pauser role to unpause");
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
    internal 
    override(ERC20UpgradeSafe, ERC20PausableUpgradeSafe)
    notBlacklisted(to)
    notBlacklisted(from)
    {
        require(to != address(this), "SwapToken: can't transfer to contract address itself");
        super._beforeTokenTransfer(from, to, amount);
    }

    function _approve(address owner, address spender, uint256 amount)
    internal 
    override(ERC20UpgradeSafe)
    notBlacklisted(owner)
    notBlacklisted(spender)
    {
        super._approve(owner, spender, amount);
    }

    function withdrawTokens(address tokenContract) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "SwapToken [withdrawTokens]: must have admin role to withdraw");
        IERC20 tc = IERC20(tokenContract);
        require(tc.transfer(_msgSender(), tc.balanceOf(address(this))), "SwapToken [withdrawTokens] Something went wrong while transferring");
    }

    function version() public pure returns (string memory) {
        return "v3";
    }

    uint256[50] private __gap;

    //BlackListing
    mapping(address => bool) internal blacklisted;
    event Blacklisted(address indexed _account);
    event UnBlacklisted(address indexed _account);

    /**
     * @dev Throws if argument account is blacklisted
     * @param _account The address to check
    */
    modifier notBlacklisted(address _account) {
        require(blacklisted[_account] == false);
        _;
    }

    /**
     * @dev Checks if account is blacklisted
     * @param _account The address to check    
    */
    function isBlacklisted(address _account) public view returns (bool) {
        return blacklisted[_account];
    }

    /**
     * @dev Adds account to blacklist
     * @param _account The address to blacklist
    */
    function blacklist(address _account) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "SwapToken [blacklist]: must have admin role to blacklist");
        blacklisted[_account] = true;
        emit Blacklisted(_account);
    }

    /**
     * @dev Removes account from blacklist
     * @param _account The address to remove from the blacklist
    */
    function unBlacklist(address _account) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "SwapToken [unBlacklist]: must have admin role to unBlacklist");
        blacklisted[_account] = false;
        emit UnBlacklisted(_account);
    }
}

